class Solution(object):
    def sampleStats(self, c):
        """
        :type c: List[int]
        :rtype: List[float]  [minimum, maximum, mean, median, and mode ]
        """
        mi, ma, mea, med, mod = None,None,None,None,None
        n,avg,a = sum(c),0,0

        for i,v in enumerate(c):
            if v == 0: continue
            if mi == None: mi = i
            if mod == None: mod = (i,v)
            avg += i*v
            ma = i
            if v > mod[1]: mod = (i,v)
            if n%2==1:  
                if a < (n+1)/2 <= a + v: med = i*1.0
            else:
                if a < n/2 <= a + v: m1 = i
                if a < n/2 + 1 <= a + v: m2 = i
            a += v
        mea = avg*1.0/n
        if n%2== 0: med = (m1 + m2)/2.0
        return [mi,ma,mea,med,mod[0]]

